                df: {
                    fieldname: 'g_ncf',
                    fieldtype: 'Check',
                    label: __('Comprobante Fiscal'),

		onchange: () => {
				let current_value=isg_ncf();
				//console.log(isg_ncf()+' Current value');
				cur_frm.set_value("generate_ncf",current_value);
				//console.log('sweetxxx-'+cur_frm.doc.generate_ncf);
