frappe.ui.form.on('Stock Reconciliation Item',{
barcode: function(frm, cdt, cdn){
var child = locals[cdt][cdn];

if(child.item_code){
	child.warehouse=frm.doc.warehouse;
	var row = $('div[data-idx='+child.idx+']');
	console.log('got item code '+ child.barcode+' --->idx '+child.idx);
	row.find('div[data-fieldname="qty"]').find('input').select();


frappe.call({ 
method: "frappe.client.get", 
args: { doctype: "Item Price", 
filters: { "price_list": 'Standard Selling', 
"item_code": child.item_code } },async: false,
callback: function (data) { 
if (!data.exc) { 
var price = data.message.price_list_rate;
frm.set_value(child.item_tax, 110);
frm.refresh_field("items"); 
child.precio_bruto = price; 
} 
} 
});

frappe.call({ 
method: "frappe.client.get", 
args: { 
doctype: "Item", 
filters: { "item_code": child.item_code } },async: false,
callback: function (data) { 
if (!data.exc) {
var tax = data.message.taxes[0].tax_rate; 
child.item_tax=tax; 
} 
} 
});
}
child.precio_total = child.precio_bruto * (child.item_tax/100 + 1);
console.log(child.precio_total);
cur_frm.add_child("items");
cur_frm.refresh_field("items"); 
},
});
